<?php include("../include/db_conn.php");$sql=mysqli_query($conn,"SELECT l.id, l.first_name, l.picture, l.last_name, l.email, l.cnic_no, l.expertise, p.name FROM `lawyers` AS l INNER JOIN practiceareas AS p ON l.expertise = p.id WHERE l.approve = 1");$clutter="";if($sql===false){$clutter.="<tr><td colspan='8'>Error executing query. Check logs.</td></tr>".mysqli_error($conn);}else{if(mysqli_num_rows($sql)>0){while($row=mysqli_fetch_assoc($sql)){$clutter.="\n            <tr>\n                <td>".$row['id']."</td>\n                <td><img src='../assets/images/lawyers/".$row['picture']."' alt=''></td>\n                <td>".$row['first_name'].' '.$row['last_name']."</td>\n                <td>".$row['email']."</td>\n                <td>".$row['name']."</td>\n                <td>".$row['cnic_no']."</td>\n                <td><a href='view-profile.php?id=".$row['id']."' class='btn btn-primary'>View</a></td>\n                <td>\n                    <form><button type='submit' name='delete' data-index='".$row['id']."' class='btn btn-danger'>Delete</button></form>\n                </td>\n            </tr>\n            ";}}else{$clutter.="<tr><td colspan='8'>No Record Found</td></tr>";}}echo $clutter; ?>