<?php include("../include/db_conn.php");$query="SELECT l.id, l.first_name, l.picture, l.last_name, l.email, l.cnic_no, l.expertise, p.name FROM `lawyers` AS l INNER JOIN practiceareas AS p ON l.expertise = p.id WHERE visible = 1 and l.approve = 0";$sql=mysqli_query($conn,$query);$output="";if($sql){if(mysqli_num_rows($sql)>0){while($row=mysqli_fetch_assoc($sql)){$output.="\n        <tr>\n            <td>".htmlspecialchars($row['id'])."</td>\n            <td><img src='../assets/images/lawyers/".htmlspecialchars($row['picture'])."' alt='' style='width: 50px; height: auto;'></td>\n            <td>".htmlspecialchars($row['first_name'])." ".htmlspecialchars($row['last_name'])."</td>\n            <td>".htmlspecialchars($row['email'])."</td>\n            <td>".htmlspecialchars($row['name'])."</td>\n            <td>".htmlspecialchars($row['cnic_no'])."</td>\n            <td><a href='view-profile.php?id=".htmlspecialchars($row['id'])."' class='btn btn-primary'>View</a></td>\n            <td>\n                <button type='submit' name='approve' data-index='".htmlspecialchars($row['id'])."' class='btn btn-success'>Approve</button>\n            </td>\n            <td>\n                <button type='submit' name='delete' data-index='".htmlspecialchars($row['id'])."' class='btn btn-danger'>Delete</button>\n            </td>\n        </tr>\n        ";}}else{$output.="\n    <tr>\n        <td colspan='9'>No Record Found</td>\n    </tr>\n    ";}}else{$output.="<tr><td colspan='9'>Error: ".mysqli_error($conn)."</td></tr>";}echo $output;